<?php/** * TravelCRM * * An open source CRM system for travel agencies * * @author		Mazvv (vitalii.mazur@gmail.com) * @license		GNU GPLv3 (http://gplv3.fsf.org)  * @link		http://www.travelcrm.org.ua */include_once APPPATH."libraries/core/Crmcontroller.php";class Curourts extends Crmcontroller {	private $jtp = array('val'=>'rid', 'scr'=>'curourt_name', 'val_p'=>'_curourts_rid', 'scr_p'=>'curourt_name');	public function __construct(){		parent::__construct();		$this->lang->load('curourts');		$this->load->model('curourts_model');				# Overwrite jtp mapper if need 		# It's very usable if form has some value_pickers with one type		if(element('val_p', $this->a_uri_assoc, null)) $this->jtp['val_p'] = element('val_p', $this->a_uri_assoc, null);		if(element('scr_p', $this->a_uri_assoc, null)) $this->jtp['scr_p'] = element('scr_p', $this->a_uri_assoc, null);	}
	public function _remap($m_Name){		switch ($m_Name) {			case 'create': {$this->create();break;}			case 'edit': {$this->edit();break;}			case 'details': {$this->details();break;}			case 'remove': {$this->remove();break;}			case 'move': {$this->move();break;}			case 'sort': {$this->sort();break;}			case 'vcreate': {$this->vcreate();break;}			case 'vedit': {$this->vedit();break;}			case 'vdetails': {$this->vdetails();break;}			case 'vremove': {$this->vremove();break;}			case 'vmove': {$this->vmove();break;}			case 'vsort': {$this->vsort();break;}			case 'vjournal': 			case 'vfind': {$this->vjournal(); break;}			case 'limit': {$this->limit();break;}			case 'help': {$this->help(); break;}			default: $this->index();		}	}

	public function journal(){		$data = array();		$data['title'] = lang('CUROURTS_TITLE');		$data['orid'] = $this->get_orid();		$data['sort'] = $this->get_session('sort');		$data['find'] = $this->find();		$data['fields']['rid'] = array('label'=>'Id', 'colwidth'=>'5%', 'sort'=>True); 		$data['fields']['curourt_name'] =  array('label'=>lang('NAME'), 'colwidth'=>'25%', 'sort'=>True);		$data['fields']['curourt_name_lat'] =  array('label'=>lang('NAME_LAT'), 'colwidth'=>'25%', 'sort'=>True);		$data['fields']['country_name'] =  array('label'=>lang('COUNTRY'), 'colwidth'=>'20%', 'sort'=>True); 		$data['fields']['archive'] = array('label'=>lang('ARCHIVE'), 'colwidth'=>'10%', 'sort'=>True, 'type'=>'yes_no'); 		$data['fields']['modifyDT'] = array('label'=>lang('MODIFYDT'), 'colwidth'=>'20%', 'sort'=>True); 		$data['tools'] = $this->get_tools(); 		$data['ds'] = $this->curourts_model->get_ds();		$data['paging'] = $this->get_paging($this->curourts_model->get_calc_rows());		return $this->load->view('standart/grid', $data, True);			}

	private function create(){		$data = array();		$this->set_validation();		$data['title'] = lang('CUROURTS_TITLE_CREATE');		$data['orid'] = $this->get_orid();		$data['success'] = null;		if ($this->form_validation->run() === True){			if($rid = $this->curourts_model->create_record()){				$this->session->set_flashdata('success', True);				redirect(get_currcontroller()."/edit/$rid", 'refresh');				return;			}			else {				$data['success'] = false;			} 		}		$data['content'] = $this->load->view('curourts/create', $data, True);		return $this->load->view('layouts/main_layout', $data);	}
	private function edit(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->set_validation();		$data['title'] = lang('CUROURTS_TITLE_EDIT');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->curourts_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->curourts_model->update_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->curourts_model->get_edit($rid);		}		$data['content'] = $this->load->view('curourts/edit', $data, True);		return $this->load->view('layouts/main_layout', $data);	}

	private function details(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$data['title'] = lang('CUROURTS_TITLE_DETAILS');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->curourts_model->get_edit($rid);		if(!$data['ds']) show_404(); 		$data['content'] = $this->load->view('curourts/details', $data, True);		return $this->load->view('layouts/main_layout', $data);	}
	private function find(){		$data['orid'] = $this->get_orid();		$this->form_validation->set_rules('curourt_name', lang('NAME'), 'trim');		$this->form_validation->set_rules('curourt_name_lat', lang('NAME_LAT'), 'trim');		$this->form_validation->set_rules('_countries_rid', lang('COUNTRY'), 'trim');		if ($this->form_validation->run() == True){			$search_rule = array();			if($this->input->post('curourt_name')) $search_rule['_curourts.curourt_name'] = $this->input->post('curourt_name');			if($this->input->post('curourt_name_lat')) $search_rule['_curourts.curourt_name_lat'] = $this->input->post('curourt_name_lat');			if($this->input->post('_countries_rid')) $search_rule['_curourts._countries_rid'] = $this->input->post('_countries_rid');			$this->set_searchrule($search_rule);		}		$data['search'] = $this->get_session('searchrule');		return $this->load->view('curourts/find', $data, True);	}

		public function check_unique_name($code){		$rid = $this->input->post('rid'); # для случая если проверка идет при редактировании		if($this->curourts_model->check_unique($code, 'name', $rid)){			$this->form_validation->set_message('check_unique_name', lang('CUROURTS_NAME_NOTUNIQUE'));			return False;		}		return True;	}
	public function check_unique_name_lat($code){		$rid = $this->input->post('rid'); # для случая если проверка идет при редактировании		if($this->curourts_model->check_unique($code, 'name_lat', $rid)){			$this->form_validation->set_message('check_unique_name_lat', lang('CUROURTS_NAME_LAT_NOTUNIQUE'));			return False;		}		return True;	}

	private function move(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->form_validation->set_rules('_employeers_rid', lang('NEW_OWNER'), 'required');		$data['title'] = lang('CUROURTS_TITLE_MOVE');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->curourts_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->curourts_model->move_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->curourts_model->get_edit($rid);		}		$data['content'] = $this->load->view('curourts/move', $data, True);		return $this->load->view('layouts/main_layout', $data);	}		private function set_validation(){		$this->form_validation->set_rules('curourt_name', lang('NAME'), 'required|trim|callback_check_unique_name');		$this->form_validation->set_rules('curourt_name_lat', lang('NAME_LAT'), 'required|trim|callback_check_unique_name_lat');		$this->form_validation->set_rules('_countries_rid', lang('COUNTRY'), 'required|trim');		$this->form_validation->set_rules('descr', lang('DESCR'), 'trim|max_length[512]');		$this->form_validation->set_rules('archive', lang('ARCHIVE'), 'trim');		return;			}			/* Value pickers */	public function vjournal(){		$data = array();		$data['title'] = lang('CUROURTS_TITLE');		$data['orid'] = $this->get_orid();		$data['sort'] = $this->get_session('sort');		$data['find'] = $this->vfind();		$data['fields']['rid'] = array('label'=>'Id', 'colwidth'=>'5%', 'sort'=>True); 		$data['fields']['curourt_name'] =  array('label'=>lang('NAME'), 'colwidth'=>'25%', 'sort'=>True);		$data['fields']['curourt_name_lat'] =  array('label'=>lang('NAME_LAT'), 'colwidth'=>'25%', 'sort'=>True);		$data['fields']['country_name'] =  array('label'=>lang('COUNTRY'), 'colwidth'=>'20%', 'sort'=>True); 		$data['fields']['archive'] = array('label'=>lang('ARCHIVE'), 'colwidth'=>'10%', 'sort'=>True, 'type'=>'yes_no'); 		$data['fields']['modifyDT'] = array('label'=>lang('MODIFYDT'), 'colwidth'=>'20%', 'sort'=>True); 		$data['jtp'] = $this->jtp;		$data['tools'] = $this->get_tools(); 		$data['ds'] = $this->curourts_model->get_ds();		$data['paging'] = $this->get_paging($this->curourts_model->get_calc_rows(), True);		$content = $this->load->view('standart/vgrid', $data, True);		$this->load->view('layouts/valuepicker_layout', array('content'=>$content));				return;	}	private function vcreate(){		$data = array();		$this->set_validation();		$data['title'] = lang('CUROURTS_TITLE_CREATE');		$data['orid'] = $this->get_orid();		$data['success'] = null;		if ($this->form_validation->run() === True){			if($rid = $this->curourts_model->create_record()){				$this->session->set_flashdata('success', True);				redirect(get_currcontroller()."/vedit/$rid", 'refresh');				return;			}			else {				$data['success'] = false;			} 		}		$data['content'] = $this->load->view('curourts/vcreate', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}	private function vedit(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->set_validation();		$data['title'] = lang('CUROURTS_TITLE_EDIT');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['jtp'] = $this->jtp;		$data['ds'] = $this->curourts_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->curourts_model->update_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->curourts_model->get_edit($rid);		}		$data['content'] = $this->load->view('curourts/vedit', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}	private function vdetails(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$data['title'] = lang('CUROURTS_TITLE_DETAILS');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['jtp'] = $this->jtp;		$data['ds'] = $this->curourts_model->get_edit($rid);		if(!$data['ds']) show_404(); 		$data['content'] = $this->load->view('curourts/vdetails', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}	private function vfind(){		$data['orid'] = $this->get_orid();		$this->form_validation->set_rules('curourt_name', lang('NAME'), 'trim');		$this->form_validation->set_rules('curourt_name_lat', lang('NAME_LAT'), 'trim');		$this->form_validation->set_rules('_countries_rid', lang('COUNTRY'), 'trim');		if ($this->form_validation->run() == True){			$search_rule = array();			if($this->input->post('curourt_name')) $search_rule['_curourts.curourt_name'] = $this->input->post('curourt_name');			if($this->input->post('curourt_name_lat')) $search_rule['_curourts.curourt_name_lat'] = $this->input->post('curourt_name_lat');			if($this->input->post('_countries_rid')) $search_rule['_curourts._countries_rid'] = $this->input->post('_countries_rid');			$this->set_searchrule($search_rule);		}		$data['search'] = $this->get_session('searchrule');		return $this->load->view('curourts/vfind', $data, True);	}	private function vmove(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->form_validation->set_rules('_employeers_rid', lang('NEW_OWNER'), 'required');		$data['title'] = lang('CUROURTS_TITLE_MOVE');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->curourts_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->curourts_model->move_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->curourts_model->get_edit($rid);		}		$data['content'] = $this->load->view('curourts/vmove', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}	
}
?>