<?php/** * TravelCRM * * An open source CRM system for travel agencies * * @author		Mazvv (vitalii.mazur@gmail.com) * @license		GNU GPLv3 (http://gplv3.fsf.org)  * @link		http://www.travelcrm.org.ua */include_once APPPATH."libraries/core/Crmcontroller.php";
class Modules extends Crmcontroller {	private $jtp = array('val'=>'rid', 'scr'=>'module_full_name', 'val_p'=>'_modules_rid', 'scr_p'=>'module_full_name');			public function __construct(){
		parent::__construct();
		$this->lang->load('modules');
		$this->load->model('modules_model');		$this->load->helper('modules');		$this->load->helper('positions');				# Overwrite jtp mapper if need 		# It's very usable if form has some value_pickers with one type		if(element('val_p', $this->a_uri_assoc, null)) $this->jtp['val_p'] = element('val_p', $this->a_uri_assoc, null);		if(element('scr_p', $this->a_uri_assoc, null)) $this->jtp['scr_p'] = element('scr_p', $this->a_uri_assoc, null);	}
	
	public function _remap($m_Name){		switch ($m_Name) {
			case 'create': {$this->create();break;}
			case 'edit': {$this->edit();break;}
			case 'details': {$this->details();break;}
			case 'remove': {$this->remove();break;}			case 'move': {$this->move();break;}
			case 'sort': {$this->sort();break;}			case 'vcreate': {$this->vcreate();break;}			case 'vedit': {$this->vedit();break;}			case 'vdetails': {$this->vdetails();break;}			case 'vremove': {$this->vremove();break;}			case 'vmove': {$this->vmove();break;}			case 'vsort': {$this->vsort();break;}			case 'vjournal': 			case 'vfind': {$this->vjournal(); break;}						case 'limit': {$this->limit();break;}			case 'help': {$this->help(); break;}			default: {$this->index();}
		}
	}
	
	public function journal(){
		$data = array();		$data['title'] = lang('MODULES_TITLE');				$data['orid'] = $this->get_orid();		$data['sort'] = $this->get_session('sort');		$data['find'] = $this->find();
		$data['fields']['rid'] = array('label'=>'Id', 'colwidth'=>'10%', 'sort'=>True); 
		$data['fields']['module_name'] =  array('label'=>lang('MODULE_NAME'), 'colwidth'=>'30%', 'sort'=>True); 
		$data['fields']['module_controller'] =  array('label'=>lang('MODULE_CONTROLLER'), 'colwidth'=>'20%', 'sort'=>True); 
		$data['fields']['archive'] = array('label'=>lang('ARCHIVE'), 'colwidth'=>'20%', 'sort'=>True, 'type'=>'yes_no'); 
		$data['fields']['modifyDT'] = array('label'=>lang('MODIFYDT'), 'colwidth'=>'20%', 'sort'=>True); 
		$data['tools'] = $this->get_tools(); 
		$data['ds'] = $this->modules_model->get_ds();		$data['paging'] = $this->get_paging($this->modules_model->get_calc_rows());
		return $this->load->view('standart/grid', $data, True);		
	}
	
	
	private function create(){		$data = array();		$this->set_validation();		$data['title'] = lang('MODULES_TITLE_CREATE');		$data['orid'] = $this->get_orid();		$data['success'] = null;		if ($this->form_validation->run() === True){			if($rid = $this->modules_model->create_record()){				$this->session->set_flashdata('success', True);				redirect(get_currcontroller()."/edit/$rid", 'refresh');				return;			}			else {				$data['success'] = false;			} 		}		$data['content'] = $this->load->view('modules/create', $data, True);		return $this->load->view('layouts/main_layout', $data);	}
	
	private function edit(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->set_validation();		$data['title'] = lang('MODULES_TITLE_EDIT');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->modules_model->update_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->modules_model->get_edit($rid);		}		$data['content'] = $this->load->view('modules/edit', $data, True);		return $this->load->view('layouts/main_layout', $data);	}
	
	private function details(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$data['title'] = lang('MODULES_TITLE_DETAILS');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		if(!$data['ds']) show_404(); 		$data['content'] = $this->load->view('modules/details', $data, True);		return $this->load->view('layouts/main_layout', $data);	}

	
	private function find(){		$data['orid'] = $this->get_orid();		$this->form_validation->set_rules('module_name', lang('MODULE_NAME'), 'trim');		$this->form_validation->set_rules('module_controller', lang('MODULE_CONTROLLER'), 'trim');		if ($this->form_validation->run() == True){			$search_rule = array();			if($this->input->post('module_name')) $search_rule['_modules.module_name'] = $this->input->post('module_name');			if($this->input->post('module_controller')) $search_rule['_modules.module_controller'] = $this->input->post('module_controller');			$this->set_searchrule($search_rule);		}		$data['search'] = $this->get_session('searchrule');		return $this->load->view('modules/find', $data, True);	}		private function move(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->form_validation->set_rules('_employeers_rid', lang('NEW_OWNER'), 'required');		$data['title'] = lang('MODULES_TITLE_MOVE');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->modules_model->move_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->modules_model->get_edit($rid);		}		$data['content'] = $this->load->view('standart/move', $data, True);		return $this->load->view('layouts/main_layout', $data);	}	/* Value Picker's actions */		public function vjournal(){		$data = array();		$data['title'] = lang('MODULES_TITLE');		$data['orid'] = $this->get_orid();		$data['sort'] = $this->get_session('sort');		$data['find'] = $this->vfind();		$data['fields']['rid'] = array('label'=>'Id', 'colwidth'=>'10%', 'sort'=>True); 		$data['fields']['module_name'] =  array('label'=>lang('MODULE_NAME'), 'colwidth'=>'30%', 'sort'=>True); 		$data['fields']['module_controller'] =  array('label'=>lang('MODULE_CONTROLLER'), 'colwidth'=>'20%', 'sort'=>True); 		$data['fields']['archive'] = array('label'=>lang('ARCHIVE'), 'colwidth'=>'20%', 'sort'=>True, 'type'=>'yes_no'); 		$data['fields']['modifyDT'] = array('label'=>lang('MODIFYDT'), 'colwidth'=>'20%', 'sort'=>True); 		$data['jtp'] = $this->jtp;		$data['tools'] = $this->get_tools(); 		$data['ds'] = $this->modules_model->get_ds();		$data['paging'] = $this->get_paging($this->modules_model->get_calc_rows(), True);		$content = $this->load->view('standart/vgrid', $data, True);		$this->load->view('layouts/valuepicker_layout', array('content'=>$content));				return;	}		private function vcreate(){		$data = array();		$this->set_validation();		$data['title'] = lang('MODULES_TITLE_CREATE');		$data['orid'] = $this->get_orid();		$data['success'] = null;		if ($this->form_validation->run() === True){			if($rid = $this->modules_model->create_record()){				$this->session->set_flashdata('success', True);				redirect(get_currcontroller()."/edit/$rid", 'refresh');				return;			}			else {				$data['success'] = false;			} 		}		$data['content'] = $this->load->view('modules/vcreate', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}		private function vedit(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->set_validation();		$data['title'] = lang('MODULES_TITLE_EDIT');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		$data['jtp'] = $this->jtp;		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->modules_model->update_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->modules_model->get_edit($rid);		}		$data['content'] = $this->load->view('modules/vedit', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}		private function vdetails(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$data['title'] = lang('MODULES_TITLE_DETAILS');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		$data['jtp'] = $this->jtp;		if(!$data['ds']) show_404(); 		$data['content'] = $this->load->view('modules/vdetails', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);	}		private function vfind(){		$data['orid'] = $this->get_orid();		$this->form_validation->set_rules('module_name', lang('MODULE_NAME'), 'trim');		$this->form_validation->set_rules('module_controller', lang('MODULE_CONTROLLER'), 'trim');		if ($this->form_validation->run() == True){			$search_rule = array();			if($this->input->post('module_name')) $search_rule['_modules.module_name'] = $this->input->post('module_name');			if($this->input->post('module_controller')) $search_rule['_modules.module_controller'] = $this->input->post('module_controller');			$this->set_searchrule($search_rule);		}		$data['search'] = $this->get_session('searchrule');		return $this->load->view('modules/vfind', $data, True);	}		private function vmove(){		$rid = (int)$this->uri->segment(3);		if(!$rid) show_404();		$data = array();		$this->form_validation->set_rules('_employeers_rid', lang('NEW_OWNER'), 'required');		$data['title'] = lang('MODULES_TITLE_MOVE');		$data['rid'] = $rid;		$data['orid'] = $this->get_orid();		$data['ds'] = $this->modules_model->get_edit($rid);		$data['success'] = $this->session->flashdata('success')?$this->session->flashdata('success'):null;		if(!$data['ds']) show_404(); 		if ($this->form_validation->run() === True){			if($this->modules_model->move_record()) $data['success'] = true;			else $data['success'] = false;			$data['ds'] = $this->modules_model->get_edit($rid);		}		$data['content'] = $this->load->view('modules/vmove', $data, True);		return $this->load->view('layouts/valuepicker_layout', $data);			}	public function check_unique_module($module_controller){		$rid = $this->input->post('rid'); # для случая если проверка идет при редактировании		if($this->modules_model->check_unique_module($module_controller, $rid)){			$this->form_validation->set_message('check_unique_module', lang('MODULE_NOTUNIQUE'));			return False;		}		return True;	}	private function set_validation(){		$this->form_validation->set_rules('module_name', lang('MODULE_NAME'), 'trim|required');		$this->form_validation->set_rules('module_controller', lang('MODULE_CONTROLLER'), 'trim|required|callback_check_unique_module');		$this->form_validation->set_rules('descr', lang('DESCR'), 'trim|max_length[512]');		$this->form_validation->set_rules('archive', lang('ARCHIVE'), 'trim');		return;			}
	}