<?phpinclude_once APPPATH."libraries/core/Crmmodel.php";class Advertisestypes_model extends Crmmodel{	public function __construct(){		parent::__construct();	}
	public function get_ds(){		$this->db->select('SQL_CALC_FOUND_ROWS _advertisestypes.rid as rid, 							_advertisestypes.type_name as type_name,  							DATE_FORMAT(_advertisestypes.modifyDT, \'%d.%m.%Y\') as modifyDT, 							_advertisestypes.descr as descr, _advertisestypes.archive', False);		$this->db->from('_advertisestypes');		if($searchRule = $this->ci->get_session('searchrule')) $this->db->like($searchRule);		if($sort = $this->ci->get_session('sort'))	$this->db->orderby($sort['c'], $sort['r']);		$this->db->limit($this->ci->config->item('crm_grid_limit'), element('p', $this->ci->a_uri_assoc, null));		$query = $this->db_get('_cities');		return $query->num_rows()?$query->result():array();	}
	public function get_edit($rid){		$this->db->select('_advertisestypes.rid as rid, 							_advertisestypes.type_name as type_name,  							_advertisestypes.modifyDT as modifyDT, 							_advertisestypes.descr as descr, _advertisestypes.owner_users_rid,							_advertisestypes.archive');		$this->db->from('_advertisestypes');		$this->db->where(array('_advertisestypes.rid'=>$rid));		$query = $this->db_get('_advertisestypes');		return $query->num_rows()?$query->row():False;	}
		public function create_record(){		$insArray = array('type_name'=>$this->ci->input->post('type_name'),							'descr'=>$this->ci->input->post('descr'),							'archive'=>$this->ci->input->post('archive'),							'owner_users_rid'=>get_curr_urid(),							'modifier_users_rid'=>get_curr_urid());		$this->db->set('createDT', 'now()', False);		$this->db->set('modifyDT', 'now()', False);		$this->db->trans_begin();		$this->db->insert('_advertisestypes', $insArray);		$insRid = $this->db->insert_id();		if ($this->db->trans_status() === FALSE){    		$this->db->trans_rollback();    		return False;		}else{    		$this->db->trans_commit();    		return $insRid;		}			}
	public function update_record(){		$update_arr = array('type_name'=>$this->ci->input->post('type_name'),							'descr'=>$this->ci->input->post('descr'),							'archive'=>$this->ci->input->post('archive'),							'modifier_users_rid'=>get_curr_urid());		$this->db->set('modifyDT', 'now()', False);		$this->db->trans_begin();		$this->db->update('_advertisestypes', $update_arr, array('rid'=>$this->ci->input->post('rid')));		if ($this->db->trans_status() === FALSE){    		$this->db->trans_rollback();    		return False;		}else{    		$this->db->trans_commit();    		return True;		}			}
	public function remove_items(){		$this->db->trans_begin();		foreach($this->ci->input->post('row') as $rid){			$this->db->delete('_advertisestypes', array('rid'=>$rid));			}		if ($this->db->trans_status() === FALSE){    		$this->db->trans_rollback();    		return False;		}else{    		$this->db->trans_commit();    		return True;		}			}		public function check_unique($val, $rid=null){		$this->db->select('count(*) as quan');		$this->db->from('_advertisestypes');		$this->db->where(array('type_name'=>$val));		if($rid) $this->db->where(array('rid != '=>$rid));		$query = $this->db->get();		return $query->num_rows()?$query->row()->quan:0;	}			public function get_list(){		$this->db->select('*');		$this->db->from('_advertisestypes');		$this->db->order_by('_advertisestypes.type_name');		$query = $this->db->get();		return $query->num_rows()?$query->result():array(); 	}			public function move_record(){		$update_doc = array('owner_users_rid'=>get_urid_byemprid($this->ci->input->post('_employeers_rid')));		$this->db->set('modifyDT', 'now()', False);		$this->db->trans_begin();		$this->db->update('_advertisestypes', $update_doc, array('_advertisestypes.rid'=>$this->ci->input->post('rid')));		if ($this->db->trans_status() === FALSE){    		$this->db->trans_rollback();    		return False;		}else{    		$this->db->trans_commit();    		return $this->ci->input->post('rid');		}			}		
}
?>