<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-6">	<?=form_label('Id', 'rid')?>	<br>	<?=form_input('rid', element('_documents.rid', $search, ''), 'id="rid" class="text part"')?></div><div class="column span-7">	<?=form_label(lang('DOC_FROM'), 'doc_from')?>	<br>	<?=form_input('doc_from', date_conv(element('_advertises_headers.date_doc >=', $search, null)), 'id="doc_from" class="text date-entry"')?>	<script type="text/javascript">		$('#doc_from').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});	</script></div><div class="column span-7 last">	<?=form_label(lang('DOC_TO'), 'doc_to')?>	<br>	<?=form_input('doc_to', date_conv(element('_advertises_headers.date_doc <=', $search, null)), 'id="doc_to" class="text date-entry"')?>		<script type="text/javascript">			$('#doc_to').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});		</script></div><div class="column span-4">	&nbsp;</div><div class="column span-6">	<?=form_label(lang('COMPANY'), 'company_name')?>	<br>	<?=get_advertisescompanies_vp(element('_advertises_headers._advertisescompanies_rid', $search, null), '_advertisescompanies_rid', 'company_name', False)?></div><div class="column span-7">	<?=form_label(lang('SOURCE'), 'source_name')?>	<br>	<?=get_advertisessources_vp(element('_advertises_headers._advertisessources_rid', $search, null), '_advertisessources_rid', 'source_name', False)?></div><div class="column span-7 last">	<?=form_label(lang('FILIAL'), 'filial_name')?>	<br>	<?=get_filials_vp(element('_emp_to_positions_rows._filials_rid', $search, null), '_filials_rid', 'filial_name', False)?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$("input[name='_filials_rid']").val('');					$('#filial_name').val('');					$("input[name='_advertisessources_rid']").val('');					$('#source_name').val('');					$("input[name='_advertisescompanies_rid']").val('');					$('#company_name').val('');					$('#rid').val('');					$('#doc_from').val('');					$('#doc_to').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
