<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-7 last">	<?=form_label(lang('COMPANY'), 'company_name')?>	<br>	<?=form_input('company_name', element('company_name', $search, ''), 'id="company_name" class="text part-5"')?></div><div class="column span-7">	<?=form_label(lang('COMPANY_TYPE'), 'company_type')?>	<br>	<?=form_dropdown('company_type', get_advcompaniestypes_list(), element('company_type', $search, ''), 'id="company_type" class="text" ')?></div><div class="column span-7 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#company_name').val('');					$('#company_type').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
