<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-10 last">	<?=form_label(lang('SOURCE'), 'source_name')?>	<br>	<?=form_input('source_name', element('source_name', $search, ''), 'id="source_name" class="text"')?></div><div class="column span-10">	<?=form_label(lang('TYPE'), 'type_name')?>	<br>	<?=form_input('type_name', element('type_name', $search, ''), 'id="type_name" class="text" style="width: 100px;"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){				$('#source_name').val('');				$('#type_name').val('');				$('#find_<?=$orid?>').submit();			});		})	</script>
