<?if($routes){?><div class="column span-24 last"><table class="routes-table">	<tr>		<th colspan="3">			<b><?=lang('AREA_FROM')?></b>		</th>		<th colspan="3">			<b><?=lang('AREA_TO')?></b>		</th>		<th>			<b><?=lang('AIRCOMPANY')?></b>		</th>		<th>			<b><?=lang('AIRCLASS')?></b>		</th>		<th>		</th>	</tr>	<? $countries_list = get_countries_list(); ?>	<? $air_classes = get_airclasses(); ?>	<? foreach($routes as $key=>$route) { ?>	<tr id="route<?=$key?>">		<td>			<?=form_hidden('_countries_rids_from[]', $route['_countries_rids_from'])?>			<?=$countries_list[$route['_countries_rids_from']]?>		</td>		<td>			<?=form_hidden('points_from[]', $route['points_from'])?>			<?=$route['points_from']?>		</td>		<td>			<?=form_hidden('departures[]', $route['departures'])?>			<?=$route['departures']?>		</td>		<td>			<?=form_hidden('_countries_rids_to[]', $route['_countries_rids_to'])?>			<?=$countries_list[$route['_countries_rids_to']]?>		</td>		<td>			<?=form_hidden('points_to[]', $route['points_to'])?>			<?=$route['points_to']?>		</td>		<td>			<?=form_hidden('arrivals[]', $route['arrivals'])?>			<?=$route['arrivals']?>		</td>		<td>			<?=form_hidden('_aircompanies_rids[]', $route['_aircompanies_rids'])?>			<?=get_aircompanyname_byrid($route['_aircompanies_rids'])?>					</td>		<td>			<?=form_hidden('air_classes[]', $route['air_classes'])?>			<?=$air_classes[$route['air_classes']]?>		</td>		<td>			<?if(!isset($readonly) || !$readonly) {?>			<a href="javascript: if(confirm('<?=lang('ROUTE_REMOVE_CONFIRM')?>')) $('#route<?=$key?>').remove();void(0);"><?=img('public/img/icons/delete_inline.gif', 'border="0"')?></a>			<? } ?>		</td>	</tr>	<? } ?></table>	</div><?}?><?if(validation_errors() && $show_errors){?><div class="error">	<?=validation_errors('<div>', '</div>');?></div>	<?}?>