<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-6">	<?=form_label(lang('COUNTRY'), '_countries_rid')?>	<br>	<?=form_dropdown('_countries_rid', get_countries_list(), element('_cities._countries_rid', $search, ''), 'id="_countries_rid" class="text"')?></div><div class="column span-7">	<?=form_label(lang('NAME'), 'city_name')?>	<br>	<?=form_input('city_name', element('_cities.city_name', $search, ''), 'id="city_name" class="text part-3"')?></div><div class="column span-7 last">	<?=form_label(lang('NAME_LAT'), 'city_name_lat')?>	<br>	<?=form_input('city_name_lat', element('_cities.city_name_lat', $search, ''), 'id="city_name_lat" class="text part-3"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#_countries_rid').val('');					$('#city_name').val('');					$('#city_name_lat').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
