<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-5">	<?=form_label(lang('L_NAME'), 'l_name')?>	<br>	<?=form_input('l_name', element('_clients.l_name', $search, ''), 'id="l_name" class="text part-3"')?></div><div class="column span-5">	<?=form_label(lang('F_NAME'), 'f_name')?>	<br>	<?=form_input('f_name', element('_clients.f_name', $search, ''), 'id="f_name" class="text part-3"')?></div><div class="column span-5">	<?=form_label(lang('FPASSP_NUM'), 'f_pass_num')?>	<br>	<?=form_input('f_pass_num', element('_clients.f_pass_num', $search, ''), 'id="f_pass_num" class="text part-3"')?></div><div class="column span-6 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_clients.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#f_name').val('');					$('#l_name').val('');					$('#f_pass_num').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
