<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'vfind_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-10">	<?=form_label(lang('L_NAME'), 'l_name')?>	<br>	<?=form_input('l_name', element('_clients.l_name', $search, ''), 'id="l_name" class="text"')?></div><div class="column span-10 last">	<?=form_label(lang('F_NAME'), 'f_name')?>	<br>	<?=form_input('f_name', element('_clients.f_name', $search, ''), 'id="f_name" class="text"')?></div><div class="column span-4">	&nbsp;</div><div class="column span-20">	<?=form_label(lang('FPASSP_NUM'), 'f_pass_num')?>	<br>	<?=form_input('f_pass_num', element('_clients.f_pass_num', $search, ''), 'id="f_pass_num" class="text" style="width:80px;"')?></div><?= form_close(); ?></div><div class="container" style="padding: 0px 0px 5px 0px; margin: 0px; text-align: right;">	<div class="column span-24 last">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#vfind_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#f_name').val('');					$('#l_name').val('');					$('#f_pass_num').val('');					$('#vfind_<?=$orid?>').submit();				});		})	</script>
