<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-5">	<?=form_label(lang('SNAME'), 'scontrahens_name')?>	<br>	<?=form_input('scontrahens_name', element('_contrahens.scontrahens_name', $search, ''), 'id="scontrahens_name" class="text part-3"')?></div><div class="column span-5">	<?=form_label(lang('NAME'), 'contrahens_name')?>	<br>	<?=form_input('contrahens_name', element('_contrahens.contrahens_name', $search, ''), 'id="contrahens_name" class="text part-4"')?></div><div class="column span-5">	<?=form_label(lang('CITY'), 'city_name')?>	<br>	<?=get_cities_vp(element('_contrahens._cities_rid', $search, null), '_cities_rid', 'city_name', False);?></div><div class="column span-6 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_contrahens.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?>	<div class="column span-24 last find-tools">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#scontrahens_name').val('');					$('#contrahens_name').val('');					$("input[name='_cities_rid']").val('');					$('#city_name').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>