<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-4">	<?=form_label(lang('CODE'), 'country_code')?>	<br>	<?=form_input('country_code', element('_countries.country_code', $search, ''), 'id="country_code" class="text part"')?></div><div class="column span-6">	<?=form_label(lang('NAME'), 'country_name')?>	<br>	<?=form_input('country_name', element('_countries.country_name', $search, ''), 'id="country_name" class="text part-3"')?></div><div class="column span-6">	<?=form_label(lang('NAME_LAT'), 'country_name_lat')?>	<br>	<?=form_input('country_name_lat', element('_countries.country_name_lat', $search, ''), 'id="country_name_lat" class="text part-3"')?></div><div class="column span-5 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_countries.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#country_code').val('');					$('#country_name').val('');					$('#country_name_lat').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
