<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-6 last">	<?=form_label(lang('COUNTRY'), '_countries_rid')?>	<br>	<?=form_dropdown('_countries_rid', get_countries_list(), element('_curourts._countries_rid', $search, ''), 'id="_countries_rid" class="text"')?></div><div class="column span-5">	<?=form_label(lang('NAME'), 'curourt_name')?>	<br>	<?=form_input('curourt_name', element('_curourts.curourt_name', $search, ''), 'id="curourt_name" class="text part-3"')?></div><div class="column span-5">	<?=form_label(lang('NAME_LAT'), 'curourt_name_lat')?>	<br>	<?=form_input('curourt_name_lat', element('_curourts.curourt_name_lat', $search, ''), 'id="curourt_name_lat" class="text part-3"')?></div><div class="column span-5 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_curourts.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#_countries_rid').val('');					$('#curourt_name').val('');					$('#curourt_name_lat').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
