<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-4">	<?=form_label(lang('CODE'), 'code')?>	<br>	<?=form_input('code', element('_currencies.code', $search, ''), 'id="code" class="text part"')?></div><div class="column span-4">	<?=form_label(lang('NAME'), 'currency_name')?>	<br>	<?=form_input('currency_name', element('_currencies.currency_name', $search, ''), 'id="currency_name" class="text part-2"')?></div><div class="column span-4">	<?=form_label(lang('LEFT_WORD'), 'left_word')?>	<br>	<?=form_input('left_word', element('_currencies.left_word', $search, ''), 'id="left_word" class="text part"')?></div><div class="column span-4">	<?=form_label(lang('RIGHT_WORD'), 'right_word')?>	<br>	<?=form_input('right_word', element('_currencies.right_word', $search, ''), 'id="right_word" class="text part"')?></div><div class="column span-5 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_currencies.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#code').val('');					$('#currency_name').val('');					$('#left_word').val('');					$('#right_word').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
