<div class="grid fieldscontainer"><h3><?=$title?></h3><?= form_open(get_currcontroller()."/create/go", array('id'=>'create_'.$orid, 'autocomplete'=>'off'))?><div class="container editform"><?if(validation_errors()){?><div class="error">	<?=validation_errors('<div>', '</div>');?></div>	<?}?><?if($success===False){?><div class="error">	<?=lang('SAVE_SYSTEM_ERROR')?></div><?}?><?if($success===True){?><div class="success">	<?=lang('SAVE_SYSTEM_SUCCESS')?></div><?}?><fieldset>	<legend><?=lang('DOCUMENT')?></legend>	<div class="column span-4">		<?=form_label(lang('DATE_DOC'), 'date_doc')?> <font color="red">*</font>	</div>	<div class="column span-20 last">		<?=form_input('date_doc', date_conv(set_value('date_doc', date('Y-m-d'))), 'id="date_doc" class="text date-entry"')?>		<script type="text/javascript">			$('#date_doc').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});		</script>	</div>	</fieldset><div class="column span-4">	<?=form_label(lang('EMPLOYEER'), 'full_name')?> <font color="red">*</font></div><div class="column span-20 last">	<?=get_employeers_vp(set_value('_employeers_rid', null))?></div><div class="column span-4">	<?=form_label(lang('FILIAL'), 'filial_name')?> <font color="red">*</font></div><div class="column span-20 last">	<?=get_filials_vp(set_value('_filials_rid', null))?></div><div class="column span-4">	<?=form_label(lang('POSITION'), '_positions_rid')?> <font color="red">*</font></div><div class="column span-20 last">	<?=form_dropdown('_positions_rid', get_positions_list(), set_value('_positions_rid', null), 'id="_positions_rid" class="text"')?></div><div class="column span-4">	<?=form_label(lang('BDATE'), 'bdate')?> <font color="red">*</font></div><div class="column span-20 last">	<?=form_input('bdate', date_conv(set_value('bdate', date('Y-m-d'))), 'id="bdate" class="text date-entry"')?>	<script type="text/javascript">		$('#bdate').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});	</script></div><fieldset><legend><?=lang('SALARY')?></legend><div class="column span-4">	<?=form_label(lang('SALARY_VAL'), 'salary')?> <font color="red">*</font></div><div class="column span-20 last">	<?=form_input('salary', set_value('salary', null), 'id="salary" class="part-2"')?></div><div class="column span-4">	<?=form_label(lang('CURRENCY'), '_currencies_rid')?> <font color="red">*</font></div><div class="column span-20 last">	<?=form_dropdown('_currencies_rid', get_currencies(), set_value('_currencies_rid', null), 'id="_currencies_rid" class="text"')?></div></fieldset><div class="column span-4">	<?=form_label(lang('ARCHIVE'), 'archive')?></div><div class="column span-20 last">	<?=form_dropdown('archive', array('0'=>lang('NO'), '1'=>lang('YES')), set_value('archive', ''), 'id="archive" class="text" ')?></div><div class="column span-4">	<?=form_label(lang('DESCR'), 'descr')?></div><div class="column span-20 last">	<?=form_textarea('descr', set_value('descr', ''), 'id="descr" class="text" style="width:200px; height: 50px;"')?></div></div>
<div class="column span-24 last controls">	<input type="submit" value="<?=lang('SAVE')?>" class="button" id="submit" name="submit"> <input type="reset" value="<?=lang('CANCEL')?>" class="button" onclick="window.location='<?=site_url(get_currcontroller()) ?>';" id="reset" name="reset"></div><?= form_close(); ?></div>