<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'vfind_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-5">	<?=form_label(lang('CITY'), 'city_name')?>	<br>	<?=get_cities_vp(element('_filials._cities_rid', $search, null), '_cities_rid', 'city_name', False);?></div><div class="column span-5">	<?=form_label(lang('CODE'), 'code')?>	<br>	<?=form_input('code', element('_filials.code', $search, ''), 'id="code" class="text part"')?></div><div class="column span-5">	<?=form_label(lang('NAME'), 'name')?>	<br>	<?=form_input('name', element('_filials.name', $search, ''), 'id="name" class="text part-4"')?></div><div class="column span-6 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_filials.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?>	<div class="column span-24 last find-tools">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready($('#archive').val('1');		function(){			$('#find_submit').click(function(){$('#vfind_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$("input[name='_cities_rid']").val('');					$('#name').val('');					$('#code').val('');					$('#archive').val('1');					$('#vfind_<?=$orid?>').submit();				});		})	</script>
