<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-10">	<?=form_label(lang('CODE'), 'code')?>	<br>	<?=form_input('code', element('_food.code', $search, ''), 'id="code" class="text part-2"')?></div><div class="column span-10 last">	<?=form_label(lang('NAME'), 'food_name')?>	<br>	<?=form_input('food_name', element('_food.food_name', $search, ''), 'id="food_name" class="text part-5"')?></div><?= form_close(); ?>	<div class="column span-24 last find-tools">		<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset">	</div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#code').val('');					$('#food_name').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
