<div class="container findform"><?= form_open(get_currcontroller()."/vfind/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-3">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-7">	<?=form_label(lang('NAME'), 'hotel_name')?>	<br>	<?=form_input('hotel_name', element('_hotels.hotel_name', $search, ''), 'id="hotel_name" class="text"')?></div><div class="column span-14 last">	<?=form_label(lang('COUNTRY'), '_countries_rid')?>	<br>	<?=form_dropdown('_countries_rid', get_countries_list(), element('_hotels._countries_rid', $search, ''), 'id="_countries_rid" class="text"')?></div><div class="clear"></div><div class="column span-3">	&nbsp;</div><div class="column span-7">	<?=form_label(lang('CUROURT'), '_curourts_rid')?>	<br>	<?=get_curourts_vp(element('_hotels._curourts_rid', $search, null), '_curourts_rid', 'curourt_name', False)?></div><div class="column span-7">	<?=form_label(lang('HOTELCAT'), '_hotelscats_rid')?>	<br>	<?=form_dropdown('_hotelscats_rid', get_hotelscats_list(), element('_hotels._hotelscats_rid', $search, ''), 'id="_hotelscats_rid" class="text"')?></div><div class="column span-7 last">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_hotels.archive', $search, ''), 'id="archive" class="text"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$("input[name='_curourts_rid']").val('');					$('#curourt_name').val('');					$('#hotel_name').val('');					$('#_countries_rid').val('');					$('#_hotelscats_rid').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})</script>
