<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-3">	<?=form_label('Id', 'rid')?>	<br>	<?=form_input('rid', element('_documents.rid', $search, ''), 'id="rid" class="text part"')?></div><div class="column span-3">	<?=form_label(lang('DOC_FROM'), 'doc_from')?>	<br>	<?=form_input('doc_from', date_conv(element('_inout_headers.date_doc >=', $search, null)), 'id="doc_from" class="text date-entry"')?>	<script type="text/javascript">		$('#doc_from').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});	</script></div><div class="column span-3">	<?=form_label(lang('DOC_TO'), 'doc_to')?>	<br>	<?=form_input('doc_to', date_conv(element('_inout_headers.date_doc <=', $search, null)), 'id="doc_to" class="text date-entry"')?>		<script type="text/javascript">			$('#doc_to').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});		</script></div><div class="column span-6 last">	<?=form_label(lang('STATE'), '_account_states_rid')?>	<br>	<?=form_dropdown('_account_states_rid', get_states_list(), element('_finjournal._account_states_rid', $search, null), 'id="_account_states_rid" class="text" ')?></div><div class="column span-5 last">	&nbsp;</div><div class="clear"></div><div class="column span-4">	&nbsp;</div><div class="column span-3">	<?=form_label(lang('OPER_DATE_FROM'), 'oper_date_from')?>	<br>	<?=form_input('oper_date_from', date_conv(element('_finjournal.oper_date >=', $search, null)), 'id="oper_date_from" class="text date-entry"')?>	<script type="text/javascript">		$('#oper_date_from').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});	</script></div><div class="column span-3">	<?=form_label(lang('OPER_DATE_TO'), 'oper_date_to')?>	<br>	<?=form_input('oper_date_to', date_conv(element('_finjournal.oper_date <=', $search, null)), 'id="oper_date_to" class="text date-entry"')?>		<script type="text/javascript">			$('#oper_date_to').dateEntry({dateFormat: '<?=$this->config->item('crm_date_entry_format')?>', spinnerImage:''});		</script></div><div class="column span-3">	<?=form_label(lang('HIDE_ARCHIVE'), 'archive')?>	<br>	<?=form_dropdown('archive', array(1=>lang('NO'), 0=>lang('YES')), element('_documents.archive', $search, ''), 'id="archive" class="text"')?></div><div class="column span-6">	<?=form_label(lang('FILIAL'), 'filial_name')?>	<br>	<?=get_filials_vp(element('_filials_rid', $search, null), '_filials_rid', 'filial_name', False)?></div><div class="column span-5 last">	<?=form_label(lang('EMPLOYEER'), 'filial_name')?>	<br>	<?=get_employeers_vp(element('_employeers.rid', $search, null), '_employeers_rid', 'full_name', False)?></div><div class="clear"></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$("input[name='_filials_rid']").val('');					$('#filial_name').val('');					$("input[name='_employeers_rid']").val('');					$('#full_name').val('');					$('#rid').val('');					$('#doc_from').val('');					$('#doc_to').val('');					$('#oper_date_from').val('');					$('#oper_date_to').val('');					$('#_account_states_rid').val('');					$('#archive').val('1');					$('#find_<?=$orid?>').submit();				});		})	</script>
