<div class="container findform"><?= form_open(get_currcontroller()."/find/go", array('id'=>'find_'.$orid, 'autocomplete'=>'off'))?><?=form_hidden('obj_rid', $orid)?><div class="column span-4">	<h6><?=lang('SEARCH_TITLE')?></h6></div><div class="column span-10">	<?=form_label(lang('MODULE_NAME'), 'module_name')?>	<br>	<?=form_input('module_name', element('_modules.module_name', $search, ''), 'id="module_name" class="text part-5"')?></div><div class="column span-10 last">	<?=form_label(lang('MODULE_CONTROLLER'), 'module_controller')?>	<br>	<?=form_input('module_controller', element('_modules.module_controller', $search, ''), 'id="module_controller" class="text part-5"')?></div><?= form_close(); ?><div class="column span-24 last find-tools">	<input type="button" value="<?=lang('GOFIND')?>" onclick="" class="button" id="find_submit" name="find_submit"> <input type="button" value="<?=lang('GOCLEAR')?>" onclick="" class="button"  id="find_reset" name="find_reset"></div></div><script type="text/javascript">$(document).ready(		function(){			$('#find_submit').click(function(){$('#find_<?=$orid?>').submit();});			$('#find_reset').click(function(){					$('#module_name').val('');					$('#module_controller').val('');					$('#find_<?=$orid?>').submit();				});		})	</script>
